import React from 'react';
import { NavLink } from 'react-router-dom';

const Navigation = () => {
    const navLinkStyles = {
        // Add your styles here
    };

    // Determine if it's mobile or desktop view
    const isMobile = window.innerWidth <= 767; // Example breakpoint for mobile view

    return (
        <nav>
            {isMobile ? (
                // Render scrollable links for mobile
                <div style={{ overflowY: 'scroll', maxHeight: '300px' }}>
                    <NavLink style={navLinkStyles} to='/'>Home</NavLink>
                    <NavLink style={navLinkStyles} to='/About'>About</NavLink>
                    <NavLink style={navLinkStyles} to='/Work'>Work</NavLink>
                    <NavLink style={navLinkStyles} to='/Contact'>Contact</NavLink>
                </div>
            ) : (
                // Render static links for desktop
                <div>
                    <NavLink style={navLinkStyles} to='/'>Home</NavLink>
                    <NavLink style={navLinkStyles} to='/About'>About</NavLink>
                    <NavLink style={navLinkStyles} to='/Work'>Work</NavLink>
                    <NavLink style={navLinkStyles} to='/Contact'>Contact</NavLink>
                </div>
            )}
        </nav>
    );
};

export default Navigation;
